import React, {useState} from 'react';

import './styles.css';
import img from '.././../assets/imagens/confirmacao.png';

function Autenticacao() {

  const [login, setLogin] = useState(true);

  function handleAutenticacao() {
    setLogin(!login);
  }

  return (
      <div id="login-container">
        {
          login ? 
          <div id="login">
            <h1>Seja bem-vindo!</h1>
            <img src={img} alt="Login efetuado com sucesso"/>
            <button onClick={handleAutenticacao}>Login</button>
          </div>
         : 
        <div id="logout">
          <h1>Por favor, faça o login.</h1>
          <button onClick={handleAutenticacao}>Logout</button>
         </div>
       }
      </div>
  )
}

export default Autenticacao;
